package br.com.caelum.vraptor.dao;

import java.util.ArrayList;

import br.com.caelum.vraptor.model.Cliente;

public class ClienteDAO {

	
	public ArrayList<Cliente> SelectAll(){
		
		ArrayList<Cliente> clientes = new ArrayList();
		
		Cliente diego = new Cliente(1, "Diego", "data:image/jpeg;base64,");
		Cliente alan = new Cliente(2 , "Alan", "data:image/jpeg;base64,");
		Cliente denner = new Cliente(3, "Denner", "data:image/jpeg;base64,");
		
		clientes.add(diego);
		clientes.add(alan);
		clientes.add(denner);
		
		return clientes;
	}
}
